// Initialize AOS
AOS.init({
    duration: 1000,
    once: true,
    offset: 100
});

// Navbar scroll effect
window.addEventListener('scroll', function() {
    const navbar = document.querySelector('.navbar');
    if (window.scrollY > 100) {
        navbar.classList.add('navbar-scrolled');
    } else {
        navbar.classList.remove('navbar-scrolled');
    }
});

// Form submission handling
document.addEventListener('DOMContentLoaded', function() {
    const admissionForm = document.getElementById('admissionForm');
    if (admissionForm) {
        admissionForm.addEventListener('submit', function(e) {
            e.preventDefault();
            
            // Basic validation
            const formData = new FormData(this);
            const requiredFields = ['first_name', 'last_name', 'email', 'phone', 'grade', 'academic_year'];
            let isValid = true;
            
            requiredFields.forEach(field => {
                if (!formData.get(field)) {
                    isValid = false;
                    document.querySelector(`[name="${field}"]`).classList.add('is-invalid');
                } else {
                    document.querySelector(`[name="${field}"]`).classList.remove('is-invalid');
                }
            });
            
            if (!document.getElementById('agreeTerms').checked) {
                isValid = false;
                document.getElementById('agreeTerms').classList.add('is-invalid');
            } else {
                document.getElementById('agreeTerms').classList.remove('is-invalid');
            }
            
            if (isValid) {
                // In a real application, you would submit the form data to the server
                alert('Thank you for your enquiry! We will contact you soon.');
                this.reset();
            } else {
                alert('Please fill in all required fields.');
            }
        });
    }

    // Add loaded class for animations
    document.body.classList.add('loaded');
});